/**
 * Catalog view layer model
 *
 * @category    Mage
 * @package     Mage_Catalog
 * @author      Magento Core Team <core@magentocommerce.com>
 */
class Openstream_CustomListing_Model_Catalog_Layer extends Mage_Catalog_Model_Layer
{
	/**
	* Atributo añadido para asignar una categoría por defecto a las colecciones personalizadas
	*/

	protected $_customProductCollection;



public function getProductCollection()
    {
		/* Añadido para asignar categoría por defecto a colecciones personalizadas */
		if(isset($this->_customProductCollection)){
    		return $this->_customProductCollection;
		}
		
		
        if (isset($this->_productCollections[$this->getCurrentCategory()->getId()])) {
            $collection = $this->_productCollections[$this->getCurrentCategory()->getId()];
        } else {
            $collection = $this->getCurrentCategory()->getProductCollection();
            $this->prepareProductCollection($collection);
            $this->_productCollections[$this->getCurrentCategory()->getId()] = $collection;
        }

        return $collection;
    }
}